import React, { Component } from 'react';
import '../App.css';
import Donation from './Donation';

class App extends Component {
  constructor(props) {
    super();
    this.state = {
      data: {}
    };
  }
  componentDidMount() {
    const getData = async () =>
      await (await fetch(
        'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=13060'
      ))
        .json()
        .then(res => {
          const data = res;
        })
        .then(data => this.setState({ donations: data.donations }));
    getData();
  }

  render() {
    const { donations } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <Donation data={donations} height="10" />
        </header>
      </div>
    );
  }
}

export default App;
