  // }

  render() {
    console.log(this.state);
    const { donations } = this.props;
    console.log(donations);
    return (
      <ul>
        {donations.map(hit => (
          <li key={hit.objectID}>
            <a href={hit.url}>{hit.title}</a>
          </li>
        ))}
      </ul>

      // <Container>
